void Create_LineMatrix(){
	for(int i= 0; i< h; i++){
		for(int j= 0; j< w; j++){
			Line_Matrix[i][j] = 0;
		}
	}
}

void Create_VoteTable(){
	for(int i= 0; i< a; i++){
		for(int j= 0; j< 2*r+ 1; j++){
			Vote_Matrix[i][j] = 0;
		}
	}
}

void HoughTransform( ){
    for(int x = 0 ; x < w ; x++ ){
        for(int y = 0 ; y < h ; y++){
            if( Image_Matrix[y][x] == 1){
                for(int i = 0; i < 180 ; i+=4){
                    // theta
                    float Angle = i*pi/180;
                    // p
                    float Distance = ((float) x)*cos(Angle) + ((float) y)*sin(Angle);
                    // round
                    int j = ceilf(Distance)+ r ;
                    Vote_Matrix[i][j]++;
                }
            }
        }
    }
}


void Get_ImageLine(){
	for(int x = 0 ; x < w ; x++ ){
	        for(int y = 0 ; y < h ; y++){
	            if( Image_Matrix[y][x] == 1){
	                for(int i = 0; i < 180 ; i+=4){
	                    // theta
	                    float Angle = i*pi/180;
	                    // p
	                    float Distance = ((float) x)*cos(Angle) + ((float) y)*sin(Angle);
	                    // round
	                    int j = ceilf(Distance)+ r ;
	                    if(Vote_Matrix[i][j] > threshold){
	                        Line_Matrix[y][x] = 1;
	                    }
	                }
	            }
	        }
	    }
};
